import React, { useState} from 'react';
import { Link } from 'react-router-dom';
import './navbar.css'; // Import the CSS file

const Navbar = () => {
    const [searchTerm, setSearchTerm] = useState('');
    const [dropdownItems, setDropdownItems] = useState([]);
    const [isLoading, setIsLoading] = useState(false);

    const handleChange = (event) => {
      const keyword = event.target.value;
      setSearchTerm(keyword);

      // Call your API with the keyword
      if (keyword) {
        setIsLoading(true);
        fetch(`http://127.0.0.1:5000/api/vantage/${keyword}`)
          .then(response => response.json())
          .then(data => {
            // Handle the API response
            data = data.data.bestMatches || []; // Array of objects
            setDropdownItems(data);
            setIsLoading(false);
          })
          .catch(error => console.log(error));
      } else {
        setDropdownItems([]);
      }
    };

  return (
    <nav>
      <ul>
        <li>
          <Link to="/">Home</Link>
        </li>
        <li>
          <Link to="/stocks">Stocks</Link>
        </li>
        <li>
          <Link to="/stock-news">Stock News</Link>
        </li>
        <li>
          <Link to="/portfolio">Portfolio</Link>
        </li>
        <li>
          <Link to="/watchlist">Watchlist</Link>
        </li>
        <li>
          <Link to="/account">Account</Link>
        </li>
        <div className="search-container">
            <input type="text" value={searchTerm} onChange={handleChange} placeholder='Search Stock'/>
            {isLoading && <ul className="dropdown">Loading...</ul>}
            {searchTerm.length > 0 && dropdownItems.length > 0 && (
              <ul className="dropdown">
                {dropdownItems.map((item, index) => (
                  <li key={index}>
                    <Link to={`/stocks/${item['1. symbol']}`}>
                      {item['2. name']}
                    </Link>
                  </li>
                ))}
              </ul>
            )}
          </div>
      </ul>
    </nav>
  );
};

export default Navbar;
