import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './StockPage.css';
import Navbar from '../components/Navbar';

const StockGraph = ({ symbol }) => {
    const [graphUrl, setGraphUrl] = useState(null);

    useEffect(() => {
      // Fetch the graph URL using the symbol from the API
      fetch(`http://127.0.0.1:5000/api/twelve/graph/${symbol}`)
        .then(response => response.blob()) // Get the response as a Blob
        .then(data => {
          // Create an object URL from the Blob
          const imageUrl = URL.createObjectURL(data);
          setGraphUrl(imageUrl);
        })
        .catch(error => console.log(error));
    }, [symbol]);

    if (!graphUrl) {
      return <div>Loading graph...</div>;
    }

    return (
      <div>
        <h3>Stock Graph</h3>
        <img src={graphUrl} alt="Stock Graph" />
      </div>
    );
  };


const StockPage = () => {
  const { symbol } = useParams();
  const [stockData, setStockData] = useState(null);

  useEffect(() => {
    // Fetch stock details using the symbol from the API
    fetch(`http://127.0.0.1:5000/api/mboum/${symbol}`)
      .then(response => response.json())
      .then(data => {
        data = data.optionChain.result[0];
        setStockData(data);
      })
      .catch(error => console.log(error));
  }, [symbol]);

  if (!stockData) {
    return <div>Loading...</div>;
  }

  return (
    <div>
        <Navbar />
        <h1>Stock Details</h1>
        <div className='wrapper'>
            <h2>{stockData.quote.longName}</h2>
            <p>Symbol: {stockData.underlyingSymbol}</p>
            <p>Price: {stockData.quote.postMarketPrice}</p>
            <p>Change: {stockData.quote.postMarketChange}</p>
            <p>Change in percent: {stockData.quote.postMarketChangePercent}</p>
            <p>Previous close: {stockData.quote.regularMarketPreviousClose}</p>
            <p>Open: {stockData.quote.regularMarketOpen}</p>
            <p>Day low: {stockData.quote.regularMarketDayLow}</p>
            <p>Day high: {stockData.quote.regularMarketDayHigh}</p>
            <p>Volume: {stockData.quote.regularMarketVolume}</p>
            <p>Average volume: {stockData.quote.averageDailyVolume3Month}</p>
            <p>Market cap: {stockData.quote.marketCap}</p>
            <p>50 day moving average: {stockData.quote.fiftyDayAverage}</p>
            <p>200 day moving average: {stockData.quote.twoHundredDayAverage}</p>
            <StockGraph symbol={symbol} />
        </div>
    </div>
  );
};

export default StockPage;
