import React, { useEffect, useState } from 'react';
import './StockNews.css';

const StockNewsPage = () => {
  const [newsData, setNewsData] = useState([]);

  useEffect(() => {
    // Fetch stock news using the API
    fetch('http://127.0.0.1:5000/api/mboum/news')
      .then(response => response.json())
      .then(data => {
        // Set the news data
        setNewsData(data);
      })
      .catch(error => console.log(error));
  }, []);

  return (
    <div className="stock-news-container">
      <h2>Stock News</h2>
      <div className="news-list">
        {newsData.map((newsItem, index) => (
          <div className="news-item" key={index}>
            <h3>{newsItem.title}</h3>
            <p>{newsItem.summary}</p>
            <p>{newsItem.published}</p>
            <a href={newsItem.url} target="_blank" rel="noopener noreferrer">Read More</a>
          </div>
        ))}
      </div>
    </div>
  );
};

export default StockNewsPage;
