import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import Navbar from '../components/Navbar';

const AccountPage = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [showPassword, setShowPassword] = useState(false);

  const hasAccount = localStorage.getItem('hasAccount') === 'true';

  const handleLogout = () => {
    localStorage.removeItem('hasAccount');
    localStorage.removeItem('email');
    navigate('/login');
  };

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        // Fetch user data from the API based on their email
        const email = localStorage.getItem('email');
        const response = await fetch(`http://127.0.0.1:5000/users/${email}`);
        const data = await response.json();
        setUser(data);
      } catch (error) {
        console.log('Error fetching user data:', error);
      }
    };

    // Check if the user has an account
    if (hasAccount) {
      // Retrieve the user's email from localStorage
      const email = localStorage.getItem('email');
      setUser({ email });

      // Fetch the user's data from the API
      if (email) {
        fetchUserData();
      }else{
        console.log('No email found');
      }
    }
  }, [hasAccount]);

  return (
    <div>
        <Navbar />
      <h1>Account Information</h1>

      {!hasAccount && (
        <div>
          <p>You don't have an account.</p>
          <p>
            <Link to="/signup">Sign up</Link> or <Link to="/login">login</Link> to create an account.
          </p>
        </div>
      )}
      {user && user.email && (
        <div>
          <p>Email: {user.email}</p>
          <p>
            Password: {showPassword ? user.password : '********'}
            <button onClick={() => setShowPassword(!showPassword)}>Show Password</button>
          </p>
          <button onClick={handleLogout}>Logout</button>
        </div>
      )}
    </div>
  );
};

export default AccountPage;
