import React, { useState , useEffect} from 'react';
import { Link } from 'react-router-dom';
import './navbar.css'; // Import the CSS file

const Navbar = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [dropdownItems, setDropdownItems] = useState([]);

  const handleChange = (event) => {
    const keyword = event.target.value;
    setSearchTerm(keyword);

    // Call your API with the keyword
    fetch(`http://127.0.0.1:5000/api/vantage/${keyword}`)
      .then(response => response.json())
      .then(data => {
        // Handle the API response
        data = data.data.bestMatches; //array of objects
        setDropdownItems(data);
      })
      .catch(error => console.log(error));
  };

    useEffect(() => {
        console.log(dropdownItems); // Logs the updated value of dropdownItems
    }, [dropdownItems]);


  return (
    <nav>
      <ul>
        <li>
          <Link to="/">Home</Link>
        </li>
        <li>
          <Link to="/stocks">Stocks</Link>
        </li>
        <li>
          <Link to="/favorites">Favorites</Link>
        </li>
        <li>
          <Link to="/stock-news">Stock News</Link>
        </li>
        <li>
          <Link to="/portfolio">Portfolio</Link>
        </li>
        <li>
          <Link to="/watchlist">Watchlist</Link>
        </li>
        <li>
          <Link to="/account">Account</Link>
        </li>
        <div className="search-container">
            <input type="text" value={searchTerm} onChange={handleChange} />
            {isLoading && <div>Loading...</div>}
            {searchTerm.length > 0 && dropdownItems.length > 0 && (
              <ul className="dropdown">
                {dropdownItems.map((item, index) => (
                  <li key={index}>
                    <Link to={`/stocks/${item['1. symbol']}`}>
                      {item['2. name']}
                    </Link>
                  </li>
                ))}
              </ul>
            )}
          </div>
      </ul>
    </nav>
  );
};

export default Navbar;
