import React, { useState, useEffect } from 'react';
import StockCard from '../components/Stock_card';
import Navbar from '../components/Navbar';
import './Popular.css';

const PopularPage = () => {
  const [stocks, setStocks] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const stocksPerPage = 30;

  useEffect(() => {
    const fetchPopularStocks = async () => {
      try {
        const response = await fetch('http://127.0.0.1:5000/api/popular_stocks');
        const data = await response.json();
        setStocks(data);
      } catch (error) {
        console.log('Error fetching popular stocks:', error);
      }
    };

    // Fetch popular stocks when the component mounts
    fetchPopularStocks();
  }, []);

  // Calculate the total number of pages based on the available stocks
  const totalPages = Math.ceil(stocks.length / stocksPerPage);

  // Handle pagination
  const goToPage = (page) => {
    setCurrentPage(page);
  };

  // Calculate the index range for the current page
  const indexOfLastStock = currentPage * stocksPerPage;
  const indexOfFirstStock = indexOfLastStock - stocksPerPage;
  const currentStocks = stocks.slice(indexOfFirstStock, indexOfLastStock);

  return (
    <div>
      <Navbar />
      <div className="popular-page">
        <h1>Portfolio</h1>
        <div className="stock-cards">
          {currentStocks.length > 0 ? (
            currentStocks.map((stock) => (
              <div key={stock.symbol}>
                <StockCard stock={stock} />
              </div>
            ))
          ) : (
            <p>No stocks in portfolio</p>
          )}
        </div>
        <div className="pagination">
          {Array.from({ length: totalPages }, (_, index) => index + 1).map((page) => (
            <button key={page} onClick={() => goToPage(page)}>
              {page}
            </button>
          ))}
        </div>
      </div>
    </div>
  );
};

export default PopularPage;
