import React, { useState } from 'react';
import './Signup.css';
import { Link, useNavigate } from 'react-router-dom';

const SignupPage = () => {
  const navigate = useNavigate(); // Get the navigate function
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSignup = (e) => {
    e.preventDefault();
    // Perform sign-up logic with email and password
    fetch('http://127.0.0.1:5000/users/signup', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({ "email" : email, "password" : password , "portfolio" : [], "watchlist" : []})
    })
    .then(response => response.json())
    .then(data => {
        if (data.error) {
            alert(data.error);
        } else {
            // After successful signup
            localStorage.setItem('hasAccount', 'true');
            localStorage.setItem('email', email);
            navigate('/');
            window.location.reload(); // Reload the page
        }
    })
  };

  return (
    <div className="signup-container">
      <h2>Sign Up</h2>
      <p>Sign up for a free account to start tracking your favorite stocks.</p>
      <p>Already have an account?</p>
      <Link to="/login">Log In</Link>
      <form onSubmit={handleSignup}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="submit">Sign Up</button>
      </form>
    </div>
  );
};

export default SignupPage;
