import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import Navbar from '../components/Navbar';

const AccountPage = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [showPassword, setShowPassword] = useState(false);

  const hasAccount = localStorage.getItem('hasAccount') === 'true';

  const handleLogout = () => {
    localStorage.removeItem('hasAccount');
    localStorage.removeItem('email');
    navigate('/login');
  };

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        // Fetch user data from the API based on their email
        const email = localStorage.getItem('email');
        const response = await fetch(`http://127.0.0.1:5000/users/${email}`);
        const data = await response.json();
        setUser(data);
      } catch (error) {
        console.log('Error fetching user data:', error);
      }
    };

    // Check if the user has an account
    if (hasAccount) {
      // Retrieve the user's email from localStorage
      const email = localStorage.getItem('email');
      setUser({ email });

      // Fetch the user's data from the API
      if (email) {
        fetchUserData();
      }else{
        console.log('No email found');
      }
    }
  }, [hasAccount]);

  return (
    <div>
  <Navbar />
  <h1 className="text-2xl font-bold mb-4">Account Information</h1>

  {!hasAccount && (
    <div className="mb-4">
      <p>You don't have an account.</p>
      <p>
        <Link to="/signup" className="text-indigo-600 hover:text-indigo-500">Sign up</Link> or <Link to="/login" className="text-indigo-600 hover:text-indigo-500">login</Link> to create an account.
      </p>
    </div>
  )}
  {user && user.email && (
    <div className="mb-4">
      <p>Email: {user.email}</p>
      <p>
        Password: {showPassword ? user.password : '********'}
        <button
          onClick={() => setShowPassword(!showPassword)}
          className="text-indigo-600 hover:text-indigo-500 ml-2">
          Show Password
        </button>
      </p>
      <button
        onClick={handleLogout}
        className="bg-red-600 hover:bg-red-500 text-white px-4 py-2 rounded-md font-semibold">
        Logout
      </button>
    </div>
  )}
</div>

  );
};

export default AccountPage;
