import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import Navbar from '../components/Navbar';

const AccountPage = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [showPassword, setShowPassword] = useState(false);

  const hasAccount = localStorage.getItem('hasAccount') === 'true';

  const handleLogout = () => {
    localStorage.removeItem('hasAccount');
    localStorage.removeItem('email');
    navigate('/login');
  };

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        // Fetch user data from the API based on their email
        const email = localStorage.getItem('email');
        const response = await fetch(`http://127.0.0.1:5000/users/${email}`);
        const data = await response.json();
        setUser(data);
      } catch (error) {
        console.log('Error fetching user data:', error);
      }
    };

    // Check if the user has an account
    if (hasAccount) {
      // Retrieve the user's email from localStorage
      const email = localStorage.getItem('email');
      setUser({ email });

      // Fetch the user's data from the API
      if (email) {
        fetchUserData();
      }else{
        console.log('No email found');
      }
    }
  }, [hasAccount]);

  return (
    <div>
  <Navbar />

  {!hasAccount && (
    <div className="mb-4">
      <p>You don't have an account.</p>
      <p>
        <Link to="/signup" className="text-indigo-600 hover:text-indigo-500">Sign up</Link> or <Link to="/login" className="text-indigo-600 hover:text-indigo-500">login</Link> to create an account.
      </p>
    </div>
  )}

  {user && user.email && (
    <div className="bg-white shadow overflow-hidden sm:rounded-lg">
      <div className="px-4 py-5 sm:p-6">
        <h3 className="text-lg font-semibold leading-6 text-gray-900">Applicant Information</h3>
        <p className="mt-1 max-w-2xl text-sm leading-5 text-gray-500">Personal details and application.</p>
      </div>

      <div className="border-t border-gray-100">
        <dl>
          <div className="px-4 py-5 sm:grid sm:grid-cols-3 sm:gap-4 sm:p-6">
            <dt className="text-sm font-medium leading-5 text-gray-500">Email</dt>
            <dd className="mt-1 text-sm leading-5 text-gray-900 sm:col-span-2">{user.email}</dd>
          </div>

          <div className="px-4 py-5 sm:grid sm:grid-cols-3 sm:gap-4 sm:p-6">
            <dt className="text-sm font-medium leading-5 text-gray-500">Password</dt>
            <dd className="mt-1 text-sm leading-5 text-gray-900 sm:col-span-2">
              {showPassword ? user.password : '********'}
              <button onClick={() => setShowPassword(!showPassword)} className="text-indigo-600 hover:text-indigo-500 ml-2">Toggle Password</button>
            </dd>
          </div>

          <div className="px-4 py-5 sm:grid sm:grid-cols-3 sm:gap-4 sm:p-6">
            <button onClick={handleLogout} className="bg-red-600 hover:bg-red-500 text-white px-4 py-2 rounded-md font-semibold">Logout</button>
          </div>
        </dl>
      </div>
    </div>
  )}
</div>


  );
};

export default AccountPage;
