import React, { useState, useEffect } from 'react';
import StockCard from '../components/Stock_card';

const StocksPage = () => {
  const [stocks, setStocks] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const stocksPerPage = 30;

  // Mock data for stocks
  const mockStocks = [
    { id: 1, symbol: 'AAPL', name: 'Apple Inc.', price: 150.25, change: 2.5, volume: 1000000 },
    { id: 2, symbol: 'GOOGL', name: 'Alphabet Inc.', price: 2800.5, change: -1.75, volume: 500000 },
    // Add more mock stock data here...
    { id: 31, symbol: 'TEST', name: 'Test Stock', price: 50.0, change: 1.0, volume: 200000 },
  ];

  useEffect(() => {
    // Set an interval to fetch stock data at a specific interval
    const fetchStocks = () => {
      setStocks(mockStocks);
    };

    const intervalId = setInterval(fetchStocks, 1000); // Fetch stock data every 5 seconds

    return () => {
      clearInterval(intervalId); // Clear the interval when the component unmounts
    };
  }, []); // Empty dependency array to only run once on component mount

  // Calculate the total number of pages based on the available stocks
  const totalPages = Math.ceil(stocks.length / stocksPerPage);

  // Handle pagination
  const goToPage = (page) => {
    setCurrentPage(page);
  };

  // Calculate the index range for the current page
  const indexOfLastStock = currentPage * stocksPerPage;
  const indexOfFirstStock = indexOfLastStock - stocksPerPage;
  const currentStocks = stocks.slice(indexOfFirstStock, indexOfLastStock);

  return (
    <div>
      {/* Render your navbar component here */}
      <div className="search-bar">
        {/* Render your search bar component here */}
      </div>
      <div className="stock-cards">
        {currentStocks.map((stock) => (
          <StockCard key={stock.id} stock={stock} />
        ))}
      </div>
      <div className="pagination">
        {Array.from({ length: totalPages }, (_, index) => index + 1).map((page) => (
          <button key={page} onClick={() => goToPage(page)}>
            {page}
          </button>
        ))}
      </div>
    </div>
  );
};

export default StocksPage;
