import React, { useState, useEffect } from 'react';
import StockCard from '../components/Stock_card';

const StocksPage = () => {
  const [stocks, setStocks] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const stocksPerPage = 30;

  useEffect(() => {
    // Fetch stock data from the API
    fetch('http://127.0.0.1:5000/api/twelve')
      .then(response => response.json())
      .then(data => {
        if (data && Array.isArray(data)) {
            console.log(data);
          // Flatten the array of arrays
          const flattenedStocks = data.flat();
          setStocks(flattenedStocks);
        }
      })
      .catch(error => console.log(error));
  }, []);

  // Calculate the total number of pages based on the available stocks
  const totalPages = Math.ceil(stocks.length / stocksPerPage);

  // Handle pagination
  const goToPage = (page) => {
    setCurrentPage(page);
  };

  // Calculate the index range for the current page
  const indexOfLastStock = currentPage * stocksPerPage;
  const indexOfFirstStock = indexOfLastStock - stocksPerPage;
  const currentStocks = stocks.slice(indexOfFirstStock, indexOfLastStock);
  console.log(currentStocks); // length is 0 here
  return (
    <div>
      {/* Render your navbar component here */}
      <div className="search-bar">
        {/* Render your search bar component here */}
      </div>
      <div className="stock-cards">
        {currentStocks.map((stock) => (
          <StockCard key={stock.id} stock={stock} />
        ))}
      </div>
      <div className="pagination">
        {Array.from({ length: totalPages }, (_, index) => index + 1).map((page) => (
          <button key={page} onClick={() => goToPage(page)}>
            {page}
          </button>
        ))}
      </div>
    </div>
  );
};

export default StocksPage;
